import { MikroOrmModule } from "@mikro-orm/nestjs";
import { Module } from "@nestjs/common";
import { ConfigModule, ConfigService } from "@nestjs/config";
import { GraphQLModule } from "@nestjs/graphql";
import { LoggerModule } from "nestjs-pino";
import { AuthenticationModule, UserModule } from "@/modules";
import { envSchema } from "@/utils/env.schema";
import { getConfiguration } from "./mikro-orm.config";
let ApplicationModule = class ApplicationModule {
};
ApplicationModule = __decorate([
    Module({
        imports: [
            LoggerModule.forRoot({
                pinoHttp: {
                    base: { name: "Billz" },
                    messageKey: "message",
                    autoLogging: process.env.NODE_ENV === "production",
                    level: process.env.NODE_ENV !== "production" ? "debug" : "info",
                    prettyPrint: process.env.NODE_ENV !== "production"
                        ? {
                            translateTime: "dd/mm/yyyy, hh:MM:ss:l",
                            ignore: "context,pid,req",
                            levelFirst: true,
                        }
                        : undefined,
                },
            }),
            ConfigModule.forRoot({
                isGlobal: true,
                validationSchema: envSchema,
            }),
            GraphQLModule.forRootAsync({
                inject: [ConfigService],
                useFactory: async (configService) => ({
                    autoSchemaFile: configService.get("GRAPHQL_FILE"),
                    sortSchema: true,
                    cors: true,
                    context: ({ req, res }) => ({ req, res }),
                }),
            }),
            MikroOrmModule.forRootAsync({
                inject: [ConfigService],
                useFactory: async (configService) => getConfiguration(configService),
            }),
            AuthenticationModule,
            UserModule,
        ],
    })
], ApplicationModule);
export { ApplicationModule };
